var ubigeoElectores=function(){
	var a=function(a,b,c,d,e){
			return $.post("procesos/solicitudes-cpr-ubigeo.php",{
					case:a,depa:b,prov:c,dist:d,anio:$("#cboAnio").val()
				},
				function(a){
					$(e).html(a)
				})
			},
			aa=function(){
				a(0,0,0,0,"#cboAnio"),
				$("#cboAnio").change(function(){
					$("#cboAnio option:selected").each(function(){
						$("#cboDEP").empty().html('<option value="">DEPARTAMENTO</option>'),
						$("#cboPRO").empty().html('<option value="">PROVINCIA</option>'),
						$("#cboDIS").empty().html('<option value="">DISTRITO</option>'),
						//a(1,$(this).val(),0,0,"#cboPRO")
						a(1,$(this).val(),0,0,"#cboDEP")
					})
				})
			},
			b=function(){
				//a(1,0,0,0,"#cboDEP"),
				$("#cboDEP").change(function(){
					$("#cboDEP option:selected").each(function(){
						$("#cboPRO").empty().html('<option value="">PROVINCIA</option>'),
						$("#cboDIS").empty().html('<option value="">DISTRITO</option>'),
						a(2,$(this).val(),0,0,"#cboPRO")
					})
				})
			},
			c=function(){
				$("#cboPRO").change(function(){
					$("#cboPRO option:selected").each(function(){
						$("#cboDIS").empty().html('<option value="">DISTRITO</option>'),
						a(3,$("#cboDEP").val(),$(this).val(),0,"#cboDIS")
					})
				})
			};
			return{
				init:function(){
					aa(),b(),c()
				}
			}
}();
	$(document).ready(function(){
		ubigeoElectores.init();
	});