var firmasElectores=function(){
	var a='<tr><td colspan="7"><div class="alert alert-info" style="margin-bottom: 0px;"><strong>Debes seleccionar alguna opción para mostrar la información correspondiente.</strong></div></td></tr>',
	aa=function(){
		$("#cboAnio").change(function(){
			$("#data-electores").html(a)
		})
	}
	b=function(a,b,c,d,e){
		return $.post("procesos/solicitudes-cpr.php",{
			case:a,depa:b,prov:c,dist:d,anio:$("#cboAnio").val()
		},function(a){
			$(e).html(a)
			})
	},
	c=function(){
		$("#cboDEP").change(function(){
			$("#cboDEP option:selected").each(function(){
				""!==$(this).val()?b(1,$(this).val(),0,0,"#data-electores"):$("#data-electores").html(a)
			})
		})
	},
	d=function(){
		$("#cboPRO").change(function(){
			$("#cboPRO option:selected").each(function(){
				""!==$(this).val()?b(2,$("#cboDEP").val(),$(this).val(),0,"#data-electores"):b(1,$("#cboDEP").val(),0,0,"#data-electores")
			})
		})
	},
	e=function(){
		$("#cboDIS").change(function(){
			$("#cboDIS option:selected").each(function(){
				""!==$(this).val()?b(3,$("#cboDEP").val(),$("#cboPRO").val(),$(this).val(),"#data-electores"):b(2,$("#cboDEP").val(),$("#cboPRO").val(),0,"#data-electores")
			})
		})
	};
	return{
		init:function(){
			aa(),c(),d(),e()}
		}
}();
$(document).ready(function(){
	firmasElectores.init()
});